mapper.update:
update room
set room_id = #{roomId,jdbcType=VARCHAR},
  room_name = #{roomName,jdbcType=VARCHAR},
  seat_info = #{seatInfo,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, room_id, room_name, seat_info
from room
where id = #{id,jdbcType=BIGINT}

select id, room_id, room_name, seat_info
from room
where room_id = #{roomId}
